import numpy as npimport matplotlib.pyplot as pltL = 61.5;n = 49;dL = L/(n-1);rhoA = 279.3362857142857;r2   = 322.295918367347;EA  = 2574200204.0816;GJ  = 871478163.2653061;EIf = 3311103877.5510206;EIe = 4820819387.755102;M = np.eye(6)M[0,0] = rhoAM[1,1] = rhoAM[2,2] = rhoAM[3,3] = r2M[4,4] = r2M[5,5] = r2+r2K = np.eye(6)K[0,0] = EA/10K[1,1] = EA/10K[2,2] = EA;K[3,3] = EIeK[4,4] = EIfK[5,5] = GJfid  = open("bd_primary_uniform.inp","w");fid.write("""--------- BEAMDYN with OpenFAST INPUT FILE -------------------------------------------NREL 5MW blade primary input file---------------------- SIMULATION CONTROL --------------------------------------False         Echo             - Echo input data to "<RootName>.ech"? (flag)False         QuasiStaticInit  - Use quasistatic pre-conditioning with centripetal accelerations in initialization? (flag) [dynamic solve only]          0   rhoinf           - Numerical damping parameter for generalized-alpha integrator          2   quadrature       - Quadrature method: 1=Gaussian; 2=Trapezoidal (switch)"DEFAULT"     refine           - Refinement factor for trapezoidal quadrature (-) [DEFAULT = 1; used only when quadrature=2]"DEFAULT"     n_fact           - Factorization frequency for the Jacobian in N-R iteration(-) [DEFAULT = 5]"DEFAULT"     DTBeam           - Time step size (s)"DEFAULT"     load_retries     - Number of factored load retries before quitting the aimulation [DEFAULT = 20]"DEFAULT"     NRMax            - Max number of iterations in Newton-Raphson algorithm (-) [DEFAULT = 10]"DEFAULT"     stop_tol         - Tolerance for stopping criterion (-) [DEFAULT = 1E-5]FALSE         tngt_stf_fd      - Use finite differenced tangent stiffness matrix? (flag)FALSE         tngt_stf_comp    - Compare analytical finite differenced tangent stiffness matrix? (flag)"DEFAULT"     tngt_stf_pert    - Perturbation size for finite differencing (-) [DEFAULT = 1E-6]"DEFAULT"     tngt_stf_difftol - Maximum allowable relative difference between analytical and fd tangent stiffness (-); [DEFAULT = 0.1]True          RotStates        - Orient states in the rotating frame during linearization? (flag) [used only when linearizing] ---------------------- GEOMETRY PARAMETER --------------------------------------          1   member_total    - Total number of members (-)         49   kp_total        - Total number of key points (-) [must be at least 3]     1     49                 - Member number; Number of key points in this member   kp_xr         kp_yr         kp_zr        initial_twist   (m)            (m)          (m)            (deg)""");for i in range(n):    fid.write("   %2.5f %2.5f %2.5f %2.5f\n"%(0,0,i*dL,0));fid.write("""---------------------- MESH PARAMETER ------------------------------------------5   order_elem     - Order of interpolation (basis) function (-)---------------------- MATERIAL PARAMETER --------------------------------------"%s"    BldFile - Name of file containing properties for blade (quoted string)---------------------- PITCH ACTUATOR PARAMETERS -------------------------------False         UsePitchAct - Whether a pitch actuator should be used (flag)200   PitchJ      - Pitch actuator inertia (kg-m^2) [used only when UsePitchAct is true]  2E+07   PitchK      - Pitch actuator stiffness (kg-m^2/s^2) [used only when UsePitchAct is true] 500000   PitchC      - Pitch actuator damping (kg-m^2/s) [used only when UsePitchAct is true]---------------------- OUTPUTS -------------------------------------------------False          SumPrint       - Print summary data to "<RootName>.sum" (flag)"ES10.3E2"    OutFmt          - Format used for text tabular output, excluding the time channel.  2   NNodeOuts      - Number of nodes to output to file [0 - 9] (-)  1,          3    OutNd          - Nodes whose values will be output  (-)  OutList        - The next line(s) contains a list of output parameters. See OutListParameters.xlsx for a listing of available output channels, (-)"TipTDxr, TipTDyr, TipTDzr"END of input file (the word "END" must appear in the first 3 columns of this last OutList line)---------------------------------------------------------------------------------------"""%("uniform_blade.inp"))fid.close();fid = open("uniform_blade.inp",'w');fid.write("""------- BEAMDYN V1.00.* INDIVIDUAL BLADE INPUT FILE -------------------------- NREL 5MW Blade ---------------------- BLADE PARAMETERS --------------------------------------49   station_total    - Number of blade input stations (-) 1   damp_flag        - Damping flag: 0: no damping; 1: damped  ---------------------- DAMPING COEFFICIENT------------------------------------   mu1        mu2        mu3        mu4        mu5        mu6   (-)        (-)        (-)        (-)        (-)        (-)1.0E-03    1.0E-03    1.0E-03    1.0E-03    1.0E-03    1.0E-03 ---------------------- DISTRIBUTED PROPERTIES---------------------------------\n""");for i in range(n):     fid.write("%1.6f\n"%(i*dL/L));     #fid.write(np.eye(6));     #fid.write(np.eye(6));     np.savetxt(fid,K,fmt='%1.6E');     np.savetxt(fid,M,fmt='%1.6E');     fid.write("\n"); fid.close()